export default function Signup() {
  const [firstName, setName] = useState("");
  const [lastName, setLastName] = useState("");
  const [professionalEmail, setprofessionalEmail] = useState("");
  const [files, setFiles] = useState([]);
  const [image, setImage] = useState("");

  const handleChange = (event) => {
    const { name, value } = event.target;
    switch (name)
    {
      case 'firstName':
        set`{name}`(val)
    }
  };

  const handleFileChange = (event) => {
    const files = [...files, event.target.files];
    setFiles(files);
  };

  const handleImageChange = (event) => {
    setImage(event.target.file[0]);
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    const formData = new FormData();
    formData.append("name", name);
    formData.append("professionalEmail", professionalEmail);
    formData.append("image", image);
    for (const file of files) {
      formData.append("files[]", file);
    }

    axios
      .post("/api/upload", formData)
      .then((response) => {
        console.log(response);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <div>
      <div className="container-fluid p-0">
        <div className="row m-0">
          <div className="col-xl-7">
            {/* <img
              className="bg-img-cover bg-center img-fluid"
              src={Signupk}
              alt="looginpage"
            /> */}
          </div>
          <div className="col-xl-5 p-0">
            <div className="login-card login-dark">
              <div>
                <div>
                  <a className="logo text-start" href="index.html">
                    <img
                      className="img-fluid for-light"
                      src="../assets/images/logo/logo.png"
                      alt="looginpage"
                    />
                  </a>
                </div>
                <div className="login-main">
                  <form
                    className="theme-form"
                    encType="multipart/form-data"
                    onSubmit={handleSubmit}
                  >
                    <h4>Create your account</h4>
                    <p>Enter your personal details to create account</p>
                    <div className="form-group">
                      <label className="col-form-label pt-0">Your Name</label>
                      <div className="row g-2">
                        <div className="col-md-6">
                          <input
                            className="form-control"
                            type="text"
